function encodeCallScript(actions) {
    return actions.reduce((script, { to, data }) => {
        const address = web3_eth_abi.encodeParameter('address', to)
        const dataLength = web3_eth_abi.encodeParameter('uint256', (data.length - 2) / 2).toString('hex')

        return script + address.slice(26) + dataLength.slice(58) + data.slice(2)
    }, CALLSCRIPT_ID)
}

function encodeData() {
    // call newImmediatePayment on finance abi to encode data with 4 parms 
    let iface = new ethers.utils.Interface(abi_finance);
    // ETH/TOKEN address, receiver's address, amount (001000000000000000), optional data ""
    let args = [tokenAddress, recipient, requestedFund, optionalData];

    let encodedData = iface.functions['newImmediatePayment'].encode(args);
    node.warn({'[ Abi Encoded Data (newImmediatePayment) ]':  encodedData})

    const dataToEncode = [
        { from: from, to: aragonFinanceContractAddress, data: encodedData }
    ];

    let firstEncodedData = encodeCallScript(dataToEncode);
    node.warn({'[ First Scrript Encoded Data ]': firstEncodedData});

    const forwardEncoded = encodeForward(firstEncodedData);
    node.warn({'[ Abi Encoded Data (forward) ]' : forwardEncoded });
    //encode script again
    const action = [{
        to: VOTING_APP_ID,
        data: forwardEncoded
    }]
    const result = encodeCallScript(action);
    node.warn({'[ Second Script Encoded Data ]' : result });
    return result;

}

function encodeForward(data) {
    let iface = new ethers.utils.Interface(abi_voting);
    let args = [data];
    let encodedData = iface.functions['forward'].encode(args);
    return encodedData;
}

