async function batchExecute() {
    const options = {
      recipient: recipient,
      value: value,
      data: data,
    };

    try{
       let batch = await sdk.batchExecuteAccountTransaction(options);
       msg.payload.sdkPayload.batchExecute = batch;
       if(debug)
        node.warn(msg);
    }catch(error){
        msg.payload.sdkPayload.batchExecuteError = error;
        throw error;
    }
}

async function setAuthKey () {
