      authKey: authKey,
      type: "Owner"
  })
  msg.payload.sdkPayload.authKeyOutput = output;
  if(debug)
    node.warn(msg);
}

async function estimateBatch() {
    const refundToken = {}; // optional
    try{
        let batch = await sdk.estimateBatch(refundToken);
        msg.payload.sdkPayload.estimateBatch = batch;
        if(debug)
            node.warn(msg);
    }catch(error){
        msg.payload.sdkPayload.estimateBatchError = error;
        throw error;
    }
}

async function submitBatch() {
    const gasPrice = null; // optional
    let key;
    try{
        key = await sdk.submitBatch(gasPrice)
        msg.payload.sdkPayload.submitBatchKey = key;
        if(debug)
            node.warn(msg);
    }catch(error){
       msg.payload.sdkPayload.submitBatchError = error;
       throw error;
    }

    const subscribtion = sdk
    .state$
    .notification$
    .subscribe(notification => {
        if (notification === null) {
            // msg.payload.tempError = 'notification is null';
            // node.send(msg,false);
        }else{
            if (notification.type === NotificationTypes.RelayedTransactionUpdated && notification.payload.state === 'Sending') {
            // if (notification.type === NotificationTypes.RelayedTransactionUpdated && notification.payload.state === 'Sent') {
                msg.payload.sdkPayload.notification = notification.payload;
                msg.payload.sdkPayload.done = true;
                node.send(msg);
                node.done();
                // resolve(notification.payload.hash);
                subscribtion.unsubscribe();
                return;
            }
        }
    });
}

async function resetSdk() {
    await sdk.resetAccount();
}

async function process() {
    await createOrConnectAccount();
    if(action === "SDK_ACTION_SET_AUTH_KEY"){
        try{
            await setAuthKey(); 
            await estimateBatch();
            await submitBatch();
        }catch(error){
            msg.payload.sdkPayload.done = true;
            node.send(msg,false);
            node.done();
            await sdk.resetBatch();
        }
    } else if(action === "SDK_ACTION_EXECUTE_TRX"){
        try{
            await sdk.resetBatch();
            await batchExecute();
            await estimateBatch();
            await submitBatch();
        }catch(error){
            msg.payload.sdkPayload.done = true;
            msg.payload.sdkPayload.error = error;
            node.send(msg,false);
            node.done();
            await sdk.resetBatch();
        }
    }else {
        await resetSdk();
        msg.payload.sdkPayload.done = true;
        node.send(msg,false);
        node.done();
    } 
}

