 */
const context = {
    stage: 'prod',
    purpose: 'encrypt pks',
    origin: 'us-east-2'
}


async function encryptKey(cleartext) {
    try{
        const { result } = await encrypt(keyring, cleartext, { context })
        msg.payload.encryptedData = JSON.stringify(result)
    }catch(error){
        msg.payload.error = error;
    }
    node.send(msg);
    node.done();
}

async function decryptKey(encryptedData) {
    try{
        const { plaintext, messageHeader } = await decrypt(keyring, encryptedData)
        msg.payload.decryptedData = String.fromCharCode.apply(null, plaintext);
    }catch(error){
        msg.payload.error = error;
    }
    node.send(msg);
    node.done();

}

if(msg.payload.action === 'ENCRYPT'){
    encryptKey(msg.payload.data);
}else if(msg.payload.action === 'DECRYPT'){
    json = JSON.parse(msg.payload.data);
    var buf = Buffer.from(json.data);
    decryptKey(buf);
}

return;

posted = flow.get(msg.payload.userId.toString());
if(posted === undefined){
    flow.set(msg.payload.userId.toString(), true);
    return [msg, null]
}
return [null, msg ] ;
